import{_ as e,c as a,o as t,a4 as l}from"./chunks/framework.DQ3SZeUm.js";const S=JSON.parse('{"title":"MySQL","description":"","frontmatter":{},"headers":[],"relativePath":"md/basics/mysql/常见数据库.md","filePath":"md/basics/mysql/常见数据库.md","lastUpdated":1726069753000}'),s={name:"md/basics/mysql/常见数据库.md"},o=l('<h1 id="mysql" tabindex="-1">MySQL <a class="header-anchor" href="#mysql" aria-label="Permalink to &quot;MySQL&quot;">​</a></h1><h2 id="常见的数据库" tabindex="-1">常见的数据库 <a class="header-anchor" href="#常见的数据库" aria-label="Permalink to &quot;常见的数据库&quot;">​</a></h2><p><strong>通常我们将数据库分为两类：关系型数据库和非关系型数据库</strong></p><p><strong>关系型数：MySQL、Oracle、DB2、SQL Server、Postgre SQL等</strong></p><ul><li>关系型数据通常我们会创建很多个二维数据表</li><li>数据表之间相互关联起来，形成一对一、一对多、多对多关系</li><li>之后可以利用SQL语句在多张表中查询我们所需要的数据</li></ul><p><strong>非关系型数据库：MongoDB、Redis、Memcached、HBse等</strong></p><ul><li>非关系型数据库的英文就是Not only SQL，也成为NoSQL</li><li>相当而言，非关系型数据库比较简单，存储数据也会更加自由</li><li>NoSQL是基于key-value的关系，并且查询的过程中不需要进行SQL解析</li></ul><p><strong>如何在开发中选择</strong></p><ul><li>目前在公司进行后端开发，还是以关系型数据库为主</li><li>比较常见用到非关系型数据库的，在爬取大量数据进行存储时，比较常见</li></ul>',9),i=[o];function r(n,_,c,d,m,p){return t(),a("div",null,i)}const u=e(s,[["render",r]]);export{S as __pageData,u as default};
